CREATE TABLE `truyen` (
  `id_truyen` int NOT NULL AUTO_INCREMENT,
  `name` varchar(50) COLLATE utf8mb3_unicode_ci NOT NULL,
  `introduce` varchar(10000) COLLATE utf8mb3_unicode_ci NOT NULL,
  `id_author` int NOT NULL,
  `trang_thai` tinyint NOT NULL DEFAULT '0',
  `luot_doc` int DEFAULT '0',
  `rate` double DEFAULT '0',
  `image` varchar(50) COLLATE utf8mb3_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id_truyen`),
  KEY `fk_author_idx` (`id_author`),
  CONSTRAINT `fk_author` FOREIGN KEY (`id_author`) REFERENCES `author` (`id_author`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_unicode_ci;
